import React from 'react'
import styles from './Form.module.scss'

const Form = (props) => {
    const inputTextHandler = (e) =>{
        const {value} = e.target;
        console.log(value)
        props.setInputText(value)
    };

    const submitTodoHandler = (e) =>{
        e.preventDefault();
        props.setTodos=([...props.todos, {text: props.inputText, completed: false, id: Math.random()*1000}])
        props.setInputText('');

    }


    return (
        <form className={styles.form}>
            <input onChange={inputTextHandler} value={props.inputText} className={styles.input} type="text" />
            <button onClick={submitTodoHandler} className={styles.button} type="submit"> 
                +
            </button>

            <div className={styles.div}>
                <select name="todos" className={styles.todos}> 
                    <option value="all"> All </option>
                    <option value="completed"> Completed </option>
                    <option value="uncompleted"> Uncompleted </option>
                </select>
            </div>

        </form>
    )
}

export default Form;
